int up_and_down(const char *input)
{
    int floor = 0;

    int i = 0;
    while (input[i] != '\0')
    {
        switch (input[i])
        {
        case '(':
            floor++;
            break;
        case ')':
            floor--;
            break;
        }
        i++;
    }

    return floor;
}

int gone_under(const char *input)
{
    int floor = 0;

    int i = 0;
    while (input[i] != '\0')
    {
        switch (input[i])
        {
        case '(':
            floor++;
            break;
        case ')':
            floor--;
            break;
        }
        i++;
        if (floor < 0) {
            return i;
        }
    }

    return floor;
}

void part1(){
    printf("%d", up_and_down(puzzle_input));
}

void part2(){
    printf("%d", gone_under(puzzle_input));
}

void tests()
{
    assert(up_and_down("(())") == 0);
    assert(up_and_down("()()") == 0);
    assert(up_and_down("(((") == 3);
    assert(up_and_down("(()(()(") == 3);
    assert(up_and_down("))(((((") == 3);
    assert(up_and_down("())") == -1);
    assert(up_and_down("))(") == -1);
    assert(up_and_down(")))") == -3);
    assert(up_and_down(")())())") == -3);

    assert(gone_under(")") == 1);
    assert(gone_under("()())") == 5);


    printf("Tests passed.");
}

int main(int argc, char *argv[])
{
    int mode;
    if (argc == 2)
    {
        sscanf(argv[1], "%d", &mode);
    }
    else
    {
        mode = 0;
    }

    switch (mode)
    {
    case 0:
        tests();
        break;
    case 1:
        part1();
        break;
    case 2:
        part2();
        break;
    default:
        tests();
    }
    printf("\n");
}